(function($) { $('html').removeClass('no-js'); $('header a').click(function(e) { if ($(this).hasClass('no-scroll')) return; e.preventDefault(); var heading = $(this).attr('href'); var scrollDistance = $(heading).offset().top; $('html, body').animate({ scrollTop: scrollDistance + 'px' }, Math.abs(window.pageYOffset - $(heading).offset().top) / 3); if ($('header').hasClass('active')) { $('header, body').removeClass('active'); } }); $('#to-top').click(function() { $('html, body').animate({ scrollTop: 0 }, 500); }); $('#hero-scroll-down span').click(function() { var scrollDistance = $('#hero').next().offset().top; $('html, body').animate({ scrollTop: scrollDistance + 'px' }, 500); }); $('#mobile-menu-open').click(function() { $('header, body').addClass('active'); }); $('#mobile-menu-close').click(function() { $('header, body').removeClass('active'); }); })(jQuery);